import React from 'react';
import { ArrowRight, Palette, Zap, Settings, Megaphone, Brain } from 'lucide-react';

const ServicesSection = () => {
  const services = [
    {
      id: 1,
      title: "تصميم واجهات المستخدم وتجربة المستخدم",
      description: "نصمم واجهات جذابة وتجربة مستخدم سلسة تضمن تفاعل العملاء.",
      image: "/images/services/uiux_design.png",
      icon: Palette,
      features: [
        "تصميم احترافي مخصص",
        "تحسين تجربة المستخدم",
        "توافق عبر الأجهزة"
      ]
    },
    {
      id: 2,
      title: "الأتمتة الذكية",
      description: "حلول أتمتة متقدمة لتبسيط العمليات وزيادة الكفاءة.",
      image: "/images/services/smart_automation.png",
      icon: Zap,
      features: [
        "روبوتات المحادثة",
        "أتمتة المهام",
        "سير عمل ذكي"
      ]
    },
    {
      id: 3,
      title: "الحلول المخصصة",
      description: "نبني أنظمة مخصصة بالكامل تناسب احتياجات عملك الفريدة.",
      image: "/images/services/custom_solutions.png",
      icon: Settings,
      features: [
        "تحليل متطلباتك",
        "تطوير خاص حسب الطلب",
        "دعم مستمر وتحديثات"
      ]
    },
    {
      id: 4,
      title: "التسويق المؤتمت",
      description: "أدوات تسويقية ذكية تنفذ الحملات بشكل تلقائي وفعّال.",
      image: "/images/services/automated_marketing.png",
      icon: Megaphone,
      features: [
        "حملات بريد إلكتروني ذكية",
        "رسائل تلقائية للعملاء",
        "تتبع وتحليل النتائج"
      ]
    },
    {
      id: 5,
      title: "حلول الذكاء الاصطناعي",
      description: "نستخدم أحدث تقنيات الذكاء الاصطناعي لخدمة عملك.",
      image: "/images/services/ai_solutions.png",
      icon: Brain,
      features: [
        "إنشاء محتوى ذكي",
        "تحليل البيانات",
        "أتمتة مهام معقدة"
      ]
    }
  ];

  const handleServiceRequest = (serviceTitle: string) => {
    // التمرير إلى قسم التواصل معنا
    const contactSection = document.getElementById('contact');
    if (contactSection) {
      contactSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="services" className="py-20 bg-gradient-to-br from-gray-50 to-gray-100">
      <div className="container mx-auto px-6">
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
            خدماتنا
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
            نقدم مجموعة شاملة من الخدمات التقنية المتقدمة لمساعدة شركتك على النمو والابتكار
          </p>
        </div>

        {/* Services Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service) => (
            <div
              key={service.id}
              className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 overflow-hidden group hover:scale-105 cursor-pointer"
            >
              {/* Service Image */}
              <div className="relative h-48 bg-gradient-to-br from-primary-50 to-primary-100 overflow-hidden">
                <img
                  src={service.image}
                  alt={service.title}
                  className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                  onError={(e) => {
                    // Fallback to icon if image fails to load
                    const target = e.target as HTMLImageElement;
                    target.style.display = 'none';
                    const iconContainer = target.nextElementSibling as HTMLElement;
                    if (iconContainer) {
                      iconContainer.style.display = 'flex';
                    }
                  }}
                />
                {/* Fallback Icon */}
                <div className="absolute inset-0 hidden items-center justify-center bg-gradient-to-br from-primary-100 to-primary-200">
                  <service.icon className="w-20 h-20 text-primary-600" />
                </div>
              </div>

              {/* Service Content */}
              <div className="p-6">
                {/* Title */}
                <h3 className="text-xl font-bold text-gray-900 mb-3 leading-tight">
                  {service.title}
                </h3>

                {/* Description */}
                <p className="text-gray-600 mb-4 leading-relaxed">
                  {service.description}
                </p>

                {/* Features List */}
                <ul className="space-y-2 mb-6">
                  {service.features.map((feature, index) => (
                    <li key={index} className="flex items-start space-x-2 space-x-reverse">
                      <div className="w-2 h-2 bg-primary-500 rounded-full mt-2 flex-shrink-0"></div>
                      <span className="text-gray-700 text-sm">{feature}</span>
                    </li>
                  ))}
                </ul>

                {/* CTA Button */}
                <button
                  onClick={() => handleServiceRequest(service.title)}
                  className="w-full bg-gradient-to-r from-primary-600 to-primary-700 text-white py-3 px-6 rounded-xl font-semibold hover:from-primary-700 hover:to-primary-800 transition-all duration-300 flex items-center justify-center space-x-2 space-x-reverse group-hover:shadow-lg"
                >
                  <span>اطلب هذه الخدمة</span>
                  <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" />
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Bottom CTA */}
        <div className="text-center mt-16">
          <div className="bg-gradient-to-r from-primary-600 to-primary-700 rounded-2xl p-8 text-white">
            <h3 className="text-2xl font-bold mb-4">هل تحتاج إلى خدمة مخصصة؟</h3>
            <p className="text-blue-100 mb-6">
              نحن هنا لمساعدتك في تحديد أفضل الحلول لاحتياجات عملك
            </p>
            <button
              onClick={() => {
                const contactSection = document.getElementById('contact');
                if (contactSection) {
                  contactSection.scrollIntoView({ behavior: 'smooth' });
                }
              }}
              className="inline-flex items-center space-x-2 space-x-reverse bg-white text-primary-600 px-8 py-4 rounded-xl font-semibold hover:bg-gray-100 transition-colors duration-300"
            >
              <span>تواصل معنا الآن</span>
              <ArrowRight className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
