import React, { useEffect, useState } from 'react';
import SEOHead from './SEOHead';
import { Mail, Phone, Linkedin, Twitter, Instagram, Facebook, Send, MessageCircle } from 'lucide-react';
import { WebhookService, ContactFormData } from '../utils/webhookService';

const ContactPage = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    company: '',
    service: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState<'idle' | 'success' | 'error'>('idle');
  const [submitMessage, setSubmitMessage] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);

    // Test webhook connection on page load
    if (process.env.NODE_ENV === 'development') {
      testWebhookConnection();
    }
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Test webhook connection (for debugging)
  const testWebhookConnection = async () => {
    console.log('üß™ Testing webhook connection...');
    try {
      const testData = {
        test: true,
        timestamp: new Date().toISOString(),
        message: 'Test connection from contact form'
      };

      const response = await fetch('https://cloud.activepieces.com/api/v1/webhooks/2GjCUtFPEUQL5OnqD7gqR', {
        method: 'POST',
        mode: 'cors',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify(testData),
      });

      console.log('üß™ Test response:', {
        status: response.status,
        statusText: response.statusText,
        ok: response.ok
      });

      return response.ok;
    } catch (error) {
      console.error('üß™ Test failed:', error);
      return false;
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Form submission started');
    setIsSubmitting(true);
    setSubmitStatus('idle');
    setSubmitMessage('');

    // Validation
    if (!formData.fullName || !formData.email || !formData.service || !formData.message) {
      setSubmitStatus('error');
      setSubmitMessage('Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ© (*)');
      setIsSubmitting(false);
      return;
    }

    const webhookData: ContactFormData = {
      fullName: formData.fullName,
      email: formData.email,
      phone: formData.phone,
      company: formData.company,
      service: formData.service,
      message: formData.message,
      timestamp: new Date().toISOString(),
      source: 'contact-form'
    };

    console.log('üì§ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ•ŸÑŸâ ÿßŸÑŸàŸäÿ® ŸáŸàŸÉ:', webhookData);

    try {
      // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿÆÿØŸÖÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ© ŸÑÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const result = await WebhookService.sendContactForm(webhookData);

      if (result.success) {
        // ŸÜÿ¨ÿ≠ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ
        console.log('‚úÖ ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨ ÿ®ŸÜÿ¨ÿßÿ≠');
        setSubmitStatus('success');
        setSubmitMessage(`ÿ¥ŸÉÿ±Ÿãÿß ŸÑŸÉ ${formData.fullName}ÿå ÿ™ŸÖ ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ÿ®ŸÜÿ¨ÿßÿ≠ Ÿàÿ≥ŸÜÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÉ ŸÇÿ±Ÿäÿ®ÿßŸã ÿπŸÑŸâ ${formData.email}`);

        // ŸÖÿ≥ÿ≠ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
        setFormData({
          fullName: '',
          email: '',
          phone: '',
          company: '',
          service: '',
          message: ''
        });

        // ÿ•ÿÆŸÅÿßÿ° ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑŸÜÿ¨ÿßÿ≠ ÿ®ÿπÿØ 7 ÿ´ŸàÿßŸÜŸê
        setTimeout(() => {
          setSubmitStatus('idle');
          setSubmitMessage('');
        }, 7000);
      } else {
        // ŸÅÿ¥ŸÑ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ ŸÑŸÉŸÜ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿ≠ŸÑŸäÿßŸã
        console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨ ŸÑŸÉŸÜ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
        setSubmitStatus('error');
        setSubmitMessage('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑÿå ŸÑŸÉŸÜ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ŸÉ. ÿ≥ŸÜÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÉ ŸÇÿ±Ÿäÿ®ÿßŸã.');

        // ÿ•ÿÆŸÅÿßÿ° ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£ ÿ®ÿπÿØ 8 ÿ´ŸàÿßŸÜŸê
        setTimeout(() => {
          setSubmitStatus('idle');
          setSubmitMessage('');
        }, 8000);
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨:', error);
      setSubmitStatus('error');
      setSubmitMessage('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÇÿπ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ÿ£Ÿà ÿßŸÑÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß ŸÖÿ®ÿßÿ¥ÿ±ÿ©.');

      // ÿ•ÿÆŸÅÿßÿ° ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£ ÿ®ÿπÿØ 8 ÿ´ŸàÿßŸÜŸê
      setTimeout(() => {
        setSubmitStatus('idle');
        setSubmitMessage('');
      }, 8000);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="bg-gradient-to-br from-gray-50 to-gray-100 min-h-screen">
      <SEOHead 
        title="ÿßÿ™ÿµŸÑ ÿ®ŸÜÿß"
        description="ÿ™ŸàÿßÿµŸÑ ŸÖÿπ ŸÅÿ±ŸäŸÇ ŸÖÿ≥ÿßÿ± ŸÅŸÑŸà ŸÑŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßÿ≥ÿ™ÿ¥ÿßÿ±ÿ© ŸÖÿ¨ÿßŸÜŸäÿ© ÿ≠ŸàŸÑ ÿ≠ŸÑŸàŸÑ ÿßŸÑÿ£ÿ™ŸÖÿ™ÿ© ŸàÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä. ŸÜÿ≠ŸÜ ŸáŸÜÿß ŸÑŸÖÿ≥ÿßÿπÿØÿ™ŸÉ ŸÅŸä ÿ™ÿ≠ŸÇŸäŸÇ ÿßŸÑÿ™ÿ≠ŸàŸÑ ÿßŸÑÿ±ŸÇŸÖŸä ŸÑÿ£ÿπŸÖÿßŸÑŸÉ."
        keywords="ÿ™ŸàÿßÿµŸÑ MasarFlowÿå ÿßÿ≥ÿ™ÿ¥ÿßÿ±ÿ© ŸÖÿ¨ÿßŸÜŸäÿ©ÿå ÿÆÿØŸÖÿ© ÿßŸÑÿπŸÖŸÑÿßÿ°ÿå ÿØÿπŸÖ ÿ™ŸÇŸÜŸäÿå ÿßÿ™ÿµŸÑ ÿ®ŸÜÿßÿå ŸÖÿ≥ÿßÿ± ŸÅŸÑŸà"
      />
      <div className="container mx-auto px-6 pt-32 pb-16">
        {/* Page Header */}
        <div className="text-center mb-20">
          <h1 className="text-4xl md:text-6xl font-bold text-gray-900 mb-6">
            ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß
          </h1>
          <p className="text-xl text-gray-600 max-w-4xl mx-auto leading-relaxed">
          ÿßŸÖŸÑÿ£ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨ ÿ£ÿØŸÜÿßŸá ÿ£Ÿà ÿ™ŸàÿßÿµŸÑ ŸÖÿ®ÿßÿ¥ÿ±ÿ© ÿπÿ®ÿ± ÿßŸÑÿ®ÿ±ŸäÿØ ÿ£Ÿà ÿßŸÑŸáÿßÿ™ŸÅÿå Ÿàÿ≥ŸÜŸÉŸàŸÜ ÿ®ÿÆÿØŸÖÿ™ŸÉ.          </p>
        </div>

        {/* Contact Form - Full Width */}
        <div className="mb-20">
          <div className="bg-white rounded-2xl shadow-xl p-8 max-w-4xl mx-auto">
            <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">ÿ£ÿ±ÿ≥ŸÑ ŸÑŸÜÿß ÿ±ÿ≥ÿßŸÑÿ©</h2>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Row 1: Full Name and Email */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Full Name */}
                <div>
                  <label htmlFor="fullName" className="block text-sm font-medium text-gray-700 mb-2">
                    ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ *
                  </label>
                  <input
                    type="text"
                    id="fullName"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all text-sm"
                    placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖŸÉ ÿßŸÑŸÉÿßŸÖŸÑ"
                  />
                </div>

                {/* Email */}
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                    ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä *
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all text-sm"
                    placeholder="ÿ£ÿØÿÆŸÑ ÿ®ÿ±ŸäÿØŸÉ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
                  />
                </div>
              </div>

              {/* Row 2: Phone and Company */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Phone */}
                <div>
                  <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                    ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ
                  </label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all text-sm"
                    placeholder="ÿ£ÿØÿÆŸÑ ÿ±ŸÇŸÖ Ÿáÿßÿ™ŸÅŸÉ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                  />
                </div>

                {/* Company */}
                <div>
                  <label htmlFor="company" className="block text-sm font-medium text-gray-700 mb-2">
                    ÿßÿ≥ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ©
                  </label>
                  <input
                    type="text"
                    id="company"
                    name="company"
                    value={formData.company}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all text-sm"
                    placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿ¥ÿ±ŸÉÿ™ŸÉ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)"
                  />
                </div>
              </div>

              {/* Service Required - Full Width */}
              <div>
                <label htmlFor="service" className="block text-sm font-medium text-gray-700 mb-2">
                  ÿßŸÑÿÆÿØŸÖÿ© ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ© *
                </label>
                <select
                  id="service"
                  name="service"
                  value={formData.service}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all bg-white text-sm"
                >
                  <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑÿÆÿØŸÖÿ© ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©</option>
                  <option value="smart-automation">ÿßŸÑÿ£ÿ™ŸÖÿ™ÿ© ÿßŸÑÿ∞ŸÉŸäÿ©</option>
                  <option value="ai-solutions">ÿ≠ŸÑŸàŸÑ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä</option>
                  <option value="uiux-design">ÿßŸÑÿ™ÿµŸÖŸäŸÖ Ÿàÿ™ÿ¨ÿ±ÿ®ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ</option>
                  <option value="general-consultation">ÿßÿ≥ÿ™ÿ¥ÿßÿ±ÿ© ÿπÿßŸÖÿ©</option>
                </select>
              </div>

              {/* Message - Full Width */}
              <div>
                <label htmlFor="message" className="block text-sm font-medium text-gray-700 mb-2">
                  ÿ±ÿ≥ÿßŸÑÿ™ŸÉ *
                </label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  required
                  rows={5}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all resize-none text-sm"
                  placeholder="ÿßŸÉÿ™ÿ® ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ŸáŸÜÿß..."
                />
              </div>

              {/* Submit Button */}
              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full bg-gradient-to-r from-primary-600 to-primary-700 text-white py-4 px-8 rounded-lg font-semibold hover:from-primary-700 hover:to-primary-800 transition-all duration-300 flex items-center justify-center space-x-2 space-x-reverse disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 text-base"
              >
                {isSubmitting ? (
