import React, { useState } from 'react';
import { Menu, X, MessageCircle } from 'lucide-react';

const Header: React.FC = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const menuItems = [
    { name: 'الرئيسية', href: '/' },
    { name: 'خدماتنا', href: '/services' },
    // { name: 'حلولنا', href: '/solutions' }, // Removed
    { name: 'أعمالنا', href: '/portfolio' },
    { name: 'من نحن', href: '/about' },
    { name: 'اتصل بنا', href: '/contact' },
  ];

  const handleWhatsAppClick = () => {
    const phoneNumber = '+966501234567'; // استبدل برقمك الصحيح
    const message = 'مرحباً، أود الاستفسار عن خدماتكم';
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-white/90 backdrop-blur-md border-b border-secondary-100">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16 md:h-20">
          {/* Logo - في اليسار مع زيادة الحجم 40% - متجاوب للهواتف */}
          <div className="flex items-center">
            <div className="w-32 h-32 md:w-44 md:h-44 flex items-center justify-center">
              <img
                src="/images/masarflowloogo.png"
                alt="MasarFlow Logo"
                className="w-full h-full object-contain"
              />
            </div>
          </div>

          {/* القائمة - في الوسط */}
          <nav className="hidden lg:flex items-center space-x-8 space-x-reverse">
            {menuItems.map((item) => (
              <a
                key={item.name}
                href={item.href}
                className="text-secondary-700 hover:text-primary-600 font-medium transition-colors duration-300 relative group text-lg"
              >
                {item.name}
                <span className="absolute bottom-0 right-0 w-0 h-0.5 bg-primary-600 transition-all duration-300 group-hover:w-full"></span>
              </a>
            ))}
          </nav>

          {/* معلومات الاتصال عبر واتساب - في اليمين */}
          <div className="hidden lg:flex items-center">
            <button
              onClick={handleWhatsAppClick}
              className="flex items-center space-x-3 space-x-reverse bg-green-500 hover:bg-green-600 text-white px-4 py-3 rounded-full transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
            >
              <MessageCircle className="w-5 h-5" />
              <span className="font-medium">+966 50 123 4567</span>
            </button>
          </div>

          {/* زر القائمة للموبايل */}
          <button
            className="lg:hidden p-2"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            {isMenuOpen ? <X className="w-6 h-6 text-secondary-800" /> : <Menu className="w-6 h-6 text-secondary-800" />}
          </button>
        </div>

        {/* القائمة للموبايل */}
        {isMenuOpen && (
          <div className="lg:hidden py-4 border-t border-secondary-100">
            <nav className="flex flex-col space-y-4">
              {menuItems.map((item) => (
                <a
                  key={item.name}
                  href={item.href}
                  className="block text-secondary-700 hover:text-primary-600 font-medium transition-colors duration-300"
                  onClick={() => setIsMenuOpen(false)}
                >
                  {item.name}
                </a>
              ))}
              <div className="pt-4 border-t border-secondary-100">
                <button
                  onClick={handleWhatsAppClick}
                  className="w-full flex items-center justify-center space-x-3 space-x-reverse bg-green-500 hover:bg-green-600 text-white px-4 py-3 rounded-full transition-all duration-300"
                >
                  <MessageCircle className="w-5 h-5" />
                  <span className="font-medium">+966 50 123 4567</span>
                </button>
              </div>
            </nav>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
